function getquerystring(d){var f=new RegExp("(^|&)"+d+"=([^&]*)(&|$)");var e=window.location.search.substr(1).match(f);if(e!=null){return unescape(e[2])}return null}function clickcomment(){var e=document.getElementById("FormName");var g=document.getElementById("FormComment");var f=getquerystring("name");var h="upcomment?name="+e.value+"&comment="+g.value+"&postname="+f;window.location.href=h}function initpage(){convert2md()}function convert2md(){var c=document.getElementsByName("markdownContent");for(var d=0;d<c.length;d++){compile(c[d])}}function compile(e){var f=new showdown.Converter();var d=f.makeHtml(e.textContent);e.innerHTML=d};