function getquerystring(a){var b=new RegExp("(^|&)"+a+"=([^&]*)(&|$)");var c=window.location.search.substr(1).match(b);if(c!=null){return unescape(c[2])}return null}function clickcomment(){var c=document.getElementById("FormName");var e=document.getElementById("FormComment");var a=getquerystring("name");var b=document.getElementById("FormEmail");alert(b);var d="upcomment?name="+c.value+"&email="+b.value+"&comment="+e.value+"&postname="+a;window.location.href=d}function initpage(){convert2md()}function convert2md(){var b=document.getElementsByName("markdownContent");for(var a=0;a<b.length;a++){compile(b[a])}}function compile(c){var b=new showdown.Converter();var a=b.makeHtml(c.textContent);c.innerHTML=a};